// Local convenience function:
// revert bit sequence, such that MSB goes left.
void extractMessage (uint64& msg, uint& nofBytes,
uint64 m, uint n)
{
// revert bits:
msg = 0; mloc=m;
for (int i=0; i<n; i++)
{
msg<<(mloc&1);
mloc=mloc>>1;
}
nofBytes = n/8
}
